  </div>
</template>

<script>
import { bootstrap } from 'vue-gtag'

export default {
  data() {
    return {
      isOpen: false,
    }
  },
  created() {
    if (this.getGDPR() === null) {
      this.isOpen = true
    }
  },

  methods: {
    getGDPR() {
      if (process.browser && !!localStorage) {
        return localStorage.getItem('GDPR:accepted')
      }

      return undefined
    },
    accept() {
      if (process.browser && !!localStorage) {
        bootstrap().then(() => {
          this.isOpen = false

          localStorage.setItem('GDPR:accepted', true)
        })
      }
    },
    deny() {
      if (process.browser && !!localStorage) {
        this.isOpen = false

        localStorage.setItem('GDPR:accepted', false)
      }
    },
  },
}
</script>

<style scoped>
.cookie-box {
  bottom: 0;
  right: 0;
}
</style>
